import { Component } from '@angular/core';
import { SuperHero } from './models/super-heros';
import {SuperHeroService} from './services/super-hero.service'
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'SuperHero.UI';
  heroes: SuperHero[]=[];
  heroToEdit?: SuperHero;

  constructor(private superHeroService: SuperHeroService){}

  ngOnInit():void{
    this.superHeroService
      .getSuperHeroes()
      .subscribe((result: SuperHero[])=>(this.heroes=result));
    console.log(this.heroes)
  }

  updateHeroList(heroes:SuperHero[]){
    this.heroes = heroes;
  }

  initNewHero(){
    this.heroToEdit=new SuperHero();
  }

  editHero(hero: SuperHero){
    this.heroToEdit=hero;
  }

}
<button (click)="initNewHero()">Create New Hero</button>

<table>
  <thead>
    <th>Name</th>
    <th>First Name</th>
    <th>Last Name</th>
    <th>Place</th>
    <th></th>
  </thead>
  <tbody>
    <tr *ngFor="let hero of heroes">
      <td>{{hero.name}}</td>
      <td>{{hero.firstName}}</td>
      <td>{{hero.lastName}}</td>
      <td>{{hero.place}}</td>
      <td><button (click)="editHero(hero)">Edit</button></td>
    </tr>
  </tbody>
</table>

<app-edit-hero [hero]="heroToEdit" (heroesUpdated)="updateHeroList($event)"></app-edit-hero>
